mapper.select:
   select t.* from (
	   	SELECT tamf.* FROM tb_auth_menu_function  tamf
		WHERE 1 = 1 
		AND  (
			tamf.id = #{id}
			OR tamf.pid = #{id}
		)

		AND id != #{curId}

   ) t order by t.menuOrder asc

mapper.update:
update tb_auth_menu_function
set id = id 

,pid = #{pid}


,funId = #{funId}


,menuName = #{menuName}


,isGroup = #{isGroup}		


,iconCls = #{iconCls}


,menuOrder = #{menuOrder}

where id = #{id,jdbcType=INTEGER}
